css:
body, p, h4, h1, table, ul, ol, li, .footer, .inline-header {
    font-family: 'TH Sarabun New', sans-serif!important; 
    font-size: 16px;
    font-weight: normal;
}

.print-format {
    padding: 0px;
    margin-right: 10mm;
    margin-left: 5mm;
    margin-top: 3mm;
    margin-bottom: 0mm;
}

table {
    width: 100%;
    border-collapse: collapse;
}

th, td {
    padding: 0px;
    text-align: left;
}

th {
    color: #000000 !important;
}

.logo-container {
    text-align: left; 
    padding: 0;
    width: 100px;
    height: 100px;
    object-fit: contain;
}

.compact {
    margin: 0;
    padding: 0;
}

.col-1, .col-2, .col-3 {
    margin: 0;
}

.custom-table {
    width: 40%;
    border-collapse: collapse;
    margin-top: -10px;
    margin-bottom: -10px;
}

.custom-row {
    display: table-row;
}

.custom-col-1, .custom-col-2 {
    display: table-cell;
    padding: 0px;
    line-height: 0;
}

.custom-col-1 {
    margin: -5;
    width: 15%;
    text-align: left;
    text-indent: 50px;
}

.custom-col-2 {
    width: 850%;
    text-align: left;
    text-indent: 0px;
}

.right-align {
    text-align: right;
}

.left-align {
    text-align: left;
}

.center-align {
    text-align: center;
}

.border-right {
    border-right: 0.25px solid white;
}

.border-bottom {
    border-bottom: 0.25px solid white;
}

.with-border {
    border-bottom: 2px solid #d3d3d3;
    padding: 0;
}

.form-container {
    line-height: 2;
    font-size: 20px !important;
    font-family: 'TH Sarabun New', sans-serif;
}

.static-checkbox {
    width: 20px;           
    height: 20px;           
    border: 1px solid black; 
    display: inline-block; 
    margin-right: 10px;     
    background-color: white;
    }

.label-text {
    font-size: 18px;
    }

.checkbox-group {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 20px; 
    }

.checkbox-group-1 {
    display: flex;
    align-items: center;
    justify-content: flex-start; 
    flex-wrap: wrap;
    gap: 20px;
    margin-bottom: 8px;
    }

.footer {
    /*position: fixed;*/
    bottom: 10mm;
    left: 0;
    right: 0;
    text-align: left;
    margin-right: 10mm;
    margin-left: 5mm;
    page-break-inside: avoid;
}

.inline-header {
    display: inline-block;
    margin: 0 10px;
    border-top: 2px solid #d3d3d3;
    width: 40%;
    text-align: center;
    line-height: 30px;
    vertical-align: top;
}
.inline-footer span {
    position: relative;
    top: -12px; /* ย้ายคำให้อยู่บนเส้น */
    background: #fff; /* เพิ่มพื้นหลังสีขาวเพื่อไม่ให้เส้นทับคำ */
    padding: 0 5px; /* เพิ่มพื้นที่รอบคำ */
}
