css:
body, p, h4, h1, table, th, td, .footer, .inline-header, .document-section {
    font-family: 'TH Sarabun New', sans-serif!important; 
    font-size: 16px;
    font-weight: normal;
}

.print-format {
    padding: 0px;
    margin-right: 10mm;
    margin-left: 5mm;
    margin-top: 3mm;
    margin-bottom: 0mm;
}

.document-section {
    margin-bottom: 10mm; 
}

table {
    width: 100%;
    border-collapse: collapse;
}

th, td {
    padding: 0px;
    text-align: left;
}

th {
    color: #000000 !important;
}

.footer {
    /*position: fixed;*/
    bottom: 0mm;
    left: 0;
    right: 0;
    text-align: left;
}
.compact {
    margin: 0;
    padding: 0;
}
.container {
    display: flex;
    justify-content: space-between; 
    align-items: flex-start; 
    padding: 10px;
    }

.item {
    width: 30%; 
}

.logo-container {
    text-align: left; 
    padding: 0;
    width: 100px;
    height: 100px;
    object-fit: contain;
}
.border-right {
    border-right: 0.25px solid white;
}
.right-align {
    text-align: right;
}

.left-align {
    text-align: left;
}

.center-align {
    text-align: center;
}
.col-1, .col-2, .col-3 {
    margin: 0;

}
.col-1 {
    width: 50%;
    line-height: 0.5;
}

.col-2 {
    color:  #754595;
    width: 25%;
    text-align: right;
    line-height: 0.5;
}

.col-3 {
    width: 15%;
    text-align: right;
    line-height: 0.5;
}
.border-top-left {
    border-top: 1px solid #d3d3d3;
    padding-top: 10px;
    margin: 0;
    padding: 0 10px;
    text-align: center;
}

.border-top-right {
    border-top: 1px solid #d3d3d3;
    padding-top: 10px;
    margin: 0;
    text-align: center;
}

.custom-table {
    width: 40%;
    border-collapse: collapse;
    margin-top: -10px;
    margin-bottom: -10px;
}

.custom-row {
    display: table-row;
}

.custom-col-1, .custom-col-2, .custom-col-3 {
    display: table-cell;
    padding: 0px;
    line-height: 0.2;
}

.custom-col-1 {
    margin: -5;
    width: 30%;
    text-align: left;
    color: #754595;
    text-indent: 10px;
}

.custom-col-2 {
    margin: 10;
    width: 70%;
    text-align: left;
    text-indent: -40px;
}
.inline-header {
    display: inline-block;
    margin: 0 10px;
    border-top: 2px solid #d3d3d3;
    width: 40%;
    text-align: center;
    line-height: 30px;
    vertical-align: top;
}
.with-border {
    border-bottom: 2px solid #d3d3d3;
}
@media screen {
    .print-format {
        padding: 0px 15px; 
        zoom: 1; 
        transform-origin: top left;
    }
  }
